function convert() {
    
    var n = convertURLs(document.getElementById("content").value.split("\n"));
    validLinks = n.valid,
    invalidLinks = cleanArray(n.invalid),
    setHTML("message", getStatus(validLinks.length, invalidLinks.length)),
    setComponents(!0, validLinks),
    setComponents(!1, invalidLinks),
    document
        .getElementById("valid-open")
        .addEventListener("click", openLinks),
    document
        .getElementById("invalid-open")
        .addEventListener("click", openLinks)
}
function openLinks(n) {
    "invalid-open" == n.target.id
        ? (
            invalidLinks.length <= 10 || invalidLinks.length > 10 && confirm(CONFIRM.replace("{}", invalidLinks.length))
        ) && invalidLinks.forEach(function (n) {
            window.open(n)
        })
        : (
            validLinks.length <= 10 || validLinks.length > 10 && confirm(CONFIRM.replace("{}", validLinks.length))
        ) && validLinks.forEach(function (n) {
            window.open(n)
        })
}
function setHTML(n, e) {
    document
        .getElementById(n)
        .innerHTML = e
}
function setDisplay(n, e) {
    document
        .getElementById(n)
        .style
        .display = e
            ? "unset"
            : "none"
}
function getStatus(n, e) {
    return n + e == 0
        ? "All the links are invalid."
        : "Converted " + n + linkOrLinks(n) + " Found " + e + " invalid" +
                linkOrLinks(e)
}
function linkOrLinks(n) {
    return 1 == n
        ? " link."
        : " links."
}
function cleanArray(n) {
    var e = [];
    return n.forEach(function (n) {
        "" != n && e.push(n)
    }),
    e
}
function setComponents(n, e) {
    if (0 != e.length) {
        var i = n
            ? "valid"
            : "invalid";
        setDisplay("content", !1),
        setDisplay(i, !0);
        var t = "";
        e.forEach(function (n) {
            t += "<a href='" + n + "' target='_blank'>" + n + "</a><br>"
        }),
        setHTML(i + "-content", t),
        setHTML(i + "-hidden", t)
    }
}
function reset() {
    document
        .getElementById("content")
        .value = "",
    setDisplay("content", !0),
    setHTML("message", ""),
    setDisplay("valid", !1),
    setDisplay("invalid", !1),
    setHTML("valid-copy", "Copy to clipboard"),
    setHTML("invalid-copy", "Copy to clipboard"),
    document
        .getElementById("valid-open")
        .removeEventListener("click", openLinks),
    document
        .getElementById("invalid-open")
        .removeEventListener("click", openLinks)
}
const CONFIRM = "WARNING! Are you sure you want to open these links? Since there are {} links, " +
        "you may temporarily experience some lag in your web browser";
var validLinks,
    invalidLinks;